import 'package:flutter/material.dart';import '../../utils/app_colors.dart';Widget ImageWidget(  Function() pickImage,  String imagePath,  TextEditingController controller,) {  return Stack(    // textDirection: TextDirection.rtl,    children: [      TextField(        enabled: false,        controller: controller,        decoration: InputDecoration(            fillColor: Colors.white,            errorStyle: const TextStyle(color: AppColors.primaryColor),            border: const OutlineInputBorder(              borderRadius: BorderRadius.all(                Radius.circular(15.0),              ),              borderSide: BorderSide(color: AppColors.textFeildINputBorder),            ),            filled: true,            hintStyle: const TextStyle(              color: Colors.grey,              fontFamily: 'Inter',              fontSize: 12,            ),            hintText: (imagePath == "") ? "Upload Image" : imagePath,            contentPadding:                const EdgeInsets.symmetric(horizontal: 20, vertical: 5),            focusedBorder: const OutlineInputBorder(                borderSide: BorderSide(color: Colors.blue),                borderRadius: BorderRadius.all(                  Radius.circular(30.0),                )),            enabledBorder: const OutlineInputBorder(                borderSide: BorderSide(color: Colors.blue),                borderRadius: BorderRadius.all(                  Radius.circular(30.0),                ))),      ),      Positioned(          right: 10,          bottom: 4,          child: InkWell(            onTap: () {              pickImage();            },            child: Container(              height: 40,              width: 80,              decoration: BoxDecoration(                borderRadius: BorderRadius.circular(10),                color: AppColors.buttonColor,              ),              child: const Center(                child: Text(                  'Upload',                  style: TextStyle(                      fontSize: 12,                      color: Colors.white,                      fontWeight: FontWeight.bold),                ),              ),            ),          ))    ],  );}